app.use('/', routes);

const port = 8000; // Puoi scegliere la porta che preferisci

const server=app.listen(port, () => {
console.log(`Il server Ã¨ in esecuzione su http://localhost:${port}/`);
});

const WebSocket = require('ws');
const wss = new WebSocket.Server({ server });

let singleConnection = null;
// Gestisci la connessione WebSocket del client
wss.on('connection', (ws) => {
    ws.on('close', () => {
        console.log('Connessione WebSocket chiusa');
        singleConnection=false;
        return;
      });
    console.log('Client connesso');
    singleConnection=ws;
});
process.on('SIGINT',() => {
    console.log('\nChiusura del server...');
    try{
        if(singleConnection){
            singleConnection.send('Chiusura server');
        }
        server.close(() => {
          console.log('Server chiuso.');
          process.exit(0);
        });
    }
    catch(error){
        server.close(() => {
            console.log('Server chiuso.');
            process.exit(0);
          }); 
    }

});

module.exports=server;